import { createSlice } from '@reduxjs/toolkit'

const cartSlice = createSlice({
  name : 'cartItems',
  initialState : [],
  reducers: {

    addItem: (state, action) => {
      let addedItem = {
        id: action.payload.id,
        title: action.payload.item,
        qwt : action.payload.qwt,
      };

      const inCart = state.find((item) => item.id === addedItem.id )

      if (state.length === 0) {
        state.push(addedItem);
      } else {
        state.forEach(el => {
          console.log(el.id, addedItem.id)
          if (el.id === addedItem.id) {
            el.qwt += addedItem.qwt;
          } 
        })
      }

      state.push(addedItem);
    }


  }
})

export const { addItem } = cartSlice.actions;
export default cartSlice.reducer;
