import React from 'react';

export default function OrderItem({order}) {
  let shortOrderTitle = ''
  order.title.length >= 20 
  ? shortOrderTitle = order.title.match(/([a-zа-яё]+)/i)
  : shortOrderTitle



  return (
      <div className='item'>
      <img src={"http://localhost:3000/eCommerce_shop/img/" + order.img} alt='item_image'/>

      <h2>{shortOrderTitle}</h2>
      <b>{order.qty}</b>
      <b1>${order.price}</b1>
      </div>
  )
}
