import React from 'react';
import { useDispatch } from 'react-redux';
import { plusItem, minusItem } from '../redux/cartSlice';


export default function OrderItem({ order }) {
  const dispatch = useDispatch();
  //qtyMax - отримувати з БД кількість продукту в наявності
  // const qtyMax = order.maxAvalibleQty;

  const onClickPlus = () => {
    (order.qty < qtyMax) 
    ? dispatch(plusItem({
      id: order.id,
    }))
    : alert('Sorry, this is the maximum amount of the product that is available for sale.');
  }

  const onClickMinus = () => {
    dispatch(minusItem({
      id: order.id,
    }));
  }

  return (
    <div className='item'>
      <img src={"http://localhost:3000/eCommerce_shop/img/" + order.img} alt='item_image' />
      {order.title.length >= 20
        ? <h2>{(order.title.match(/([a-zа-яё]+)/i))[0]}</h2>
        : <h2>{order.title}</h2>}
      <div className='amount-button'>
        <button onClick={onClickPlus}> + </button>
        <b>{order.qty}</b>
        <button onClick={onClickMinus}>&minus;</button>
      </div>

      <h1>${order.price}</h1>
      <h2>{(order.qty * order.price).toFixed(2)}</h2>
    </div>
  )
}
