import React, { useState, useRef } from 'react'
import { useDispatch } from 'react-redux'
import { addItem } from '../redux/cartSlice';

export default function Item({ item }) {
  const dispatch = useDispatch();
  const [qty, setQty] = useState(1);
  const ref = useRef(null);

  const onChange = (e) => {
    let value = Number(e.target.value)
    value >= 1000
      ? alert('Too big number of quantity')
      : setQty(value)
  }

  const onClickAddButton = () => {
    dispatch(addItem({
      id: item.id,
      item: item.title,
      qty: qty,
      price: item.price,
      img: item.img
    }));
    ref.current.value = "";
    ref.placeholder = ""
  }

  return (
    <div className='item'>
      <img src={"http://localhost:3000/eCommerce_shop/img/" + item.img} alt='item_image' />
      <h2>{item.title}</h2>
      <p>{item.desc}</p>
      <div className='order'>
        <b>${item.price}</b>
        <label> Quantity: {""}
          <input type="number" ref={ref} min="1" max="100" step="1" name="qty_item" placeholder="1" onChange={onChange}></input>
        </label>
      </div>
      <div className='add-to-cart' onClick={onClickAddButton}> + </div>
    </div>
  )
}
