import React from 'react';
import OrderTotal from './OrderTotal'

export default function OrderItem({order}) {

  return (
    <>
      <div className='item'>
        <img src={"http://localhost:3000/eCommerce_shop/img/" + order.img} alt='item_image' />
        {order.title.length >= 20
          ? <h2>{(order.title.match(/([a-zа-яё]+)/i))[0]}</h2>
          : <h2>{order.title}</h2>}
        <b>{order.qty}</b>
        <h1>${order.price}</h1>
      </div>
      <OrderTotal qty = {order.qty} />
    </>
  )
}
