import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { addItem } from '../redux/cartSlice';

export default function Item({ item }) {
  const dispatch = useDispatch();
  const [qty , setQty] = useState(1);

  const onClickAddButton = () => {
    dispatch(addItem({
      id: item.id,
      item: item.title,
      qty: qty,
      price: item.price,
      img: item.img
    }))
  }

  return (
    <div className='item'>
      <img src={"http://localhost:3000/eCommerce_shop/img/" + item.img} alt='item_image' />
      <h2>{item.title}</h2>
      <p>{item.desc}</p>
      <b>${item.price}</b>
      <label>Quantity of goods:
        <input type="number" min="11" max="100" name="qty_item" step='1' required onChange={e => setQty(e.target.value)}></input>
      </label>
      <div className='add-to-cart' onClick={onClickAddButton}> + </div>
    </div>
  )
}
