import React from 'react';

export default function OrderItem({order}) {
  // let shortOrderTitle = ''
  // order.title.length >= 20 
  // ? shortOrderTitle = (order.title.match(/([a-zа-яё]+)/i))[0]
  // : shortOrderTitle = order.title

  return (
      <div className='item'>
      <img src={"http://localhost:3000/eCommerce_shop/img/" + order.img} alt='item_image'/>
      {order.title.length >= 20
      ? <h2>{(order.title.match(/([a-zа-яё]+)/i))[0]}</h2>
      : <h2>{order.title}</h2>
      }
      <b>{order.qty}</b>
      <h1>${order.price}</h1>
      <h>Sum of order </h>
      </div>
  )
}
