import { createSlice } from '@reduxjs/toolkit'

const cartSlice = createSlice({
  name : 'cartItems',
  initialState : [],
  reducers: {
    addItem: (state, action) => {
      const addedItem = {
        id:Date.now(),
        title: action.payload.item,
        qwt : 1,
      };
      if (state.length === 0) {
        state.push(addedItem);
      } else {
        state.forEach(el => {
          if (el.title === addItem.title) {
            el.qwt = state.el.qwt + addedItem.qwt
          } else {
            state.push(addedItem);
          }
        })
      }


    }
  }
})


export const { addItem } = cartSlice.actions;
export default cartSlice.reducer;
