import { createSlice } from '@reduxjs/toolkit'

const cartSlice = createSlice({
  name : 'cartItems',
  initialState : [],
  reducers: {
    addItem: (state, action) => {
      const addedItem = {
        id: action.payload.id,
        title: action.payload.item,
        qwt : 1,
      }

      if (state.length === 0) {
        state.push(addedItem);
      } else {
        state.map(el => {
          if (el['id'] === addedItem['id']) {
            let countQwt = el['qwt'] + addedItem['qwt']
            console.log
            return {...state.el, qwt: countQwt}
          } else {
            return state.push(addedItem);
          }
        })
      }
    }
  }
})

export const { addItem } = cartSlice.actions;
export default cartSlice.reducer;
