import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { addItem } from '../redux/cartSlice';

export default function Item({ item }) {
  const dispatch = useDispatch();
  const [qty, setQty] = useState(1);
  let maxItemAvalibleInitial = Number(localStorage.getItem(`${item.id}`));
  console.log(maxItemAvalible)


  const onChange = (e) => {
    let value = Number(e.target.value)
      value > 100
      ? alert('Too big number of quantity. maximum amount 100')
      : setQty(value);
  }

  const onClickAddButton = () => {
    if (maxItemAvalible > 0 ) {
      dispatch(addItem({
        id: item.id,
        item: item.title,
        qty: qty,
        price: item.price,
        img: item.img,
        maxQty : item.maxQty
      }))
      setQty(1)
      localStorage.setItem(`${item.id}`, `${item.maxQty-qty}`);
    } else {
      alert(`Sorry,maximum amount of the product that is available for sale - ${maxItemAvalible}`)
    }
  }

  return (
    <div className='item'>
      <img src={"http://localhost:3000/eCommerce_shop/img/" + item.img} alt='item_image' />
      <h2>{item.title}</h2>
      <p>{item.desc}</p>
      <div className='order'>
        <b>${item.price}</b>
        <label> Quantity:  &nbsp;
          <input type="number" min="1" max={item.maxQty} step="1" className="input" name="qty_item" onChange={onChange} value = {qty || ''}></input>
        </label>
      </div>
      <div className='add-to-cart' onClick={onClickAddButton}> + </div>
    </div>
  )
}
