import React from 'react'
import { useDispatch } from 'react-redux'
import { addItem } from '../redux/cartSlice';

export default function Item({ item }) {
  const dispatch = useDispatch();

  const onClickAddButton = () => {
    dispatch(addItem({
      id: item.id,
      item: item.title,
      qty: 1,
      price: item.price,
      img: item.img
    }))
  }

  return (
    <div className='item'>
      <img src={"http://localhost:3000/eCommerce_shop/img/" + item.img} alt='item_image' />
      <h2>{item.title}</h2>
      <p>{item.desc}</p>
      <b>${item.price}</b>
      <input type="number" placeholder="quantity" min="0" max="100" name="qty_item" step='1'></input>
      <div className='add-to-cart' onClick={onClickAddButton}> + </div>
    </div>
  )
}
