import { createSlice } from '@reduxjs/toolkit'

const cartSlice = createSlice({
  name : 'cartItems',
  initialState : [],
  reducers: {
    addItem: (state, action) => {
      const addedItem = {
         id:action.payload.item,
        title: action.payload.item,
        qwt : 1,
      };
      if (state.length === 0) {
        state.push(addedItem);
      } else {
        state.map(el => {
          if (el.title === addedItem.title) {
            return el.qwt = el['qwt'] + 1
          } else {
            return state.push(addedItem);
          }
        })
      }


    }
  }
})


export const { addItem } = cartSlice.actions;
export default cartSlice.reducer;
